import React from 'react';
var name;
var number;
var expMonth;
var expYear;
var cvc;

const SetCardInfoName = (passedName) => {
    name = passedName;
}

const SetCardInfoNumber = (passedNumber) => {
    number = passedNumber;
}

const SetCardInfoMonth = (passedExpMonth) => {
    expMonth = passedExpMonth;
}

const SetCardInfoYear = (passedExpYear) => {
    expYear = passedExpYear;
}

const SetCardInfoCVC = (passedCVC) => {
    cvc = passedCVC;
}

//Getters
const GetCardInfoName = () => {
    return name;
}

const GetCardInfoNumber = () => {
    return number;
}

const GetCardInfoMonth = () => {
    return expMonth;
}

const GetCardInfoYear = () => {
    return expYear;
}

const GetCardInfoCVC = (passedCVC) => {
    return cvc = passedCVC;
}


export default {
    SetCardInfoName, 
    SetCardInfoNumber, 
    SetCardInfoMonth, 
    SetCardInfoYear, 
    SetCardInfoCVC,
};
